  computed: {
    citySelect() {
      let value = this.city;
      value != "" ? value = value.toLowerCase().trim() : value;
      return value;
    },
    carriers() {
      const data = this.$store.getters["shipment/carriers"];
      return data;
    },
    quotations() {
      let data = this.$store.getters["shipment/quotations"];
      if (Array.isArray(data)) {
        const reorder = data.sort((a, b) => a.total_pricing - b.total_pricing);
        data = reorder;
      }
      return data;
    },
    carrito() {
      let carrito = this.$store.getters["carrito/getcar"];

      //recalcular coste de envio segun estado/ciudad.
      let city = this.city;
      city != "" ? city = city.toLowerCase().trim() : city;
      let envio_inicial = 0;
      let freeShipping = false;
      for (const cupon of carrito.cupon) {
        if (cupon.freeShipping == "SI") {
          envio_inicial = 0;
          freeShipping = true;
          break;
        }
      }
      if (freeShipping == false) {
        if (this.state == "Sonora" && city == "hermosillo") {
          this.precio_envio.shipping_price ? carrito.envio = this.precio_envio.shipping_price : carrito.envio = 50;
        } else if (this.carrier?.total_pricing) {
          carrito.envio = this.carrier.total_pricing;
          /* }else if(this.state =='Sonora'){
            carrito.envio =100 */
        } else {
          carrito.envio = envio_inicial;
        }
      }
      if (this.shipping_option == "Ordena y Recoge") {
        carrito.envio = 0;
      } else if (!isNaN(this.precio_envio.free_shipping) && this.precio_envio.free_shipping > 0) {
        if (carrito.subtotal >= this.precio_envio.free_shipping && this.state == "Sonora" && city == "hermosillo") {
          carrito.envio = 0;
        }
      }
      let peso = 0;
      for (const obj of carrito.items) {
        if (obj.item.skd_weight) {
          peso += obj.item.skd_weight * obj.cantidad;
        }
      }
      this.pesoTotal = peso;
      carrito.total = carrito.subtotal + parseFloat(carrito.envio);
      return carrito;
    },
    identity() {
      return this.$store.getters["admin/getIdentity"];
    },
    historyOptionPsl() {
      return this.$store.getters["pasarela/getHistoryOption"];
    },
    precio_envio: async function () {
      let arr = await this.$store.getters["global_params/data"];
      let filter = [];
      if (!Array.isArray(arr)) {
        return {};
      } else {
        filter = arr[0];
      }
      return filter;
    }
  },
  methods: {
    ...mapActions("carrito", ["getCart"]),
    ...mapActions("pasarela", ["setHistoryOptionPsl"]),
    ...mapActions("pasarela", ["setAddedPsl"]),
    ...mapActions("shipment", ["getCarriers"]),
    ...mapActions("shipment", ["getQuotations"]),
    ...mapActions("global_params", ["getAllInfoGlp"]),
    createCharge: async function (method, shipping_option, name, lastname, email, phone, address, country, state, city, postal_code, notes, facturar, f_postal_code, f_country, f_state, f_city, f_email, f_phone, reason_social, fiscal_address, rfc, terminos, email_list, carrier) {
      if (!terminos) {
        this.status = "error";
        this.message = "No has aceptado los términos y condiciones.";
        this.delStatus();
        return false;
      }
      if (!carrier.total_pricing && shipping_option == "Envío a domicilio" && this.citySelect != "hermosillo") {
        this.status = "error";
        this.message = "No has elejido una paquetería.";
        this.delStatus();
        return false;
      }
      this.loaded = true;
      let data = {
        _ctk: localStorage.getItem("_ctk"),
        method,
        shipping_option,
        id_user: this.identity.sub,
        name,
        lastname,
        email,
        phone,
        address,
        country,
        state,
        city,
        postal_code,
        notes,
        facturar,
        f_postal_code,
        f_country,
        f_state,
        f_city,
        f_email,
        f_phone,
        reason_social,
        fiscal_address,
        rfc,
        terminos,
        email_list,
        carrier
      };
      this.setAddedPsl(data);
      this.setHistoryOptionPsl("Openpay");
      this.viewModal = true;
    },
    calcTotal(precio, cantidad) {
      let total = precio * cantidad;
      return total.toFixed(2);
    },
    async obtenerCarritos() {
      await this.getCart();
    },
    validCarrier(name) {
      if (name == "Fedex" || name == "Estafeta" || name == "UPS" || name == "Paquetexpress" || name == "Redpack") {
        return true;
      } else {
        return false;
      }
    },
    getIdentity: async function () {
      let response = await this.$store.dispatch("admin/getData");
      return response;
    },
    delStatus: function () {
      setTimeout(() => this.delMsgs(), 2000);
    },
    delMsgs: function () {
      this.status = "";
      this.message = "";
    },
    aplicarCupon: async function (cupon) {
      //VDNQYUBWD6VNCM5OJ96X
      if (cupon != "") {
        let cliente = {
          sub: null,
          email: ""
        };
        if (this.identity?.sub) {
          cliente = this.identity;
        }
        let payload = {
          cupon,
          cliente,
          _ctk: localStorage.getItem("_ctk")
        };
        let result = await this.$store.dispatch("carrito/editItemCrt", {
          option: "add_cupon",
          item: payload
        });
        if (result.status == "error") {
          this.status = "error";
          this.message = result.message; // result.message;
          this.delStatus();
        } else {
          // success

          this.status = "success";
          this.message = "Cupón agregado con éxito.";
          this.delStatus();
          await this.obtenerCarritos();
        }
      }
    },
    deleteCupon: async function (cupon) {
      if (cupon != "") {
        let payload = {
          cupon,
          _ctk: localStorage.getItem("_ctk")
        };
        let result = await this.$store.dispatch("carrito/deleteItemCrt", {
          option: "del_cupon",
          item: payload
        });
        if (result.status == "error") {
          this.status = "error";
          this.message = result.message; // result.message;
          this.delStatus();
        } else {
          // success

          this.status = "success";
          this.message = "Cupón eliminado con éxito.";
          this.delStatus();
          await this.obtenerCarritos();
        }
      }
    }
  },
