          if (this.name_imagen2 == undefined || this.name_imagen2 == "" || this.name_imagen2 == null) {
            this.status = "error";
            this.message = "No has subido una imagen.";
            statusC2 = "error";
            this.delStatus();
          } else {
            //* subir imagen al backend

            var data = new FormData();
            data.append("image", this.image2);
            data.append("_method", "POST");
            let result = await this.$store.dispatch("blog/addItemBlo", {
              option: "blog-image",
              item: data
            });
            if (result.status == "success") {
              let array = [];
              for (const img of this.images) {
                array.push(img);
              }
              array.push(result.fileName);
              this.images = array;
              item2 = {
                imagen: result.fileName,
                tipo: this.caja2
              };
            } else {
              this.status = "error";
              this.message = result.message;
              statusC2 = "error";
              //enviar a borrar la imagen
            }
          }
        } else {
          statusC2 = "error";
        }
        if (statusC1 == "success" && statusC2 == "success") {
          let model = {
            caja: [item1, item2],
            tipo: tipo
          };
          let array = [];
          for (const bloque of this.added.body) {
            array.push(bloque);
          }
          array.push(model);
          this.added.body = array;
          this.texto = "";
          this.texto2 = "";
          this.frase_destacada = "";
          this.frase_destacada2 = "";
          this.frase = "";
          this.frase2 = "";
          this.firma = "";
          this.firma2 = "";
          this.image = "";
          this.name_imagen = "";
          this.image2 = "";
          this.name_imagen2 = "";
        } else {
          this.status = "error";
          this.message = "Una de las cajas esta vacia.";
          this.delStatus();
        }
      } else {
        this.status = "error";
        this.message = "Selecciona un tipo de bloque.";
        this.delStatus();
      }
    }
  }
